async function init(){await customElements.whenDefined("gmp-map");const map=document.querySelector("gmp-map");const marker=document.querySelector("gmp-advanced-marker");const placePicker=document.querySelector("gmpx-place-picker");const infowindow=new google.maps.InfoWindow;map.innerMap.setOptions({mapTypeControl:false,styles:[]});placePicker.addEventListener("gmpx-placechange",(()=>{const place=placePicker.value;if(!place.location){window.alert("No details available for input: '"+place.name+"'");infowindow.close();marker.position=null;return}if(place.viewport){map.innerMap.fitBounds(place.viewport)}else{map.center=place.location;map.zoom=17}const content=document.createElement("div");content.style.textAlign="center";content.innerHTML=`\n            <h3>Blady's Little World</h3>\n            <p>Visit us for a nurturing experience!</p>\n            <img src="assets/optimized/Blady_s Little World Logo.png" alt="Marker Image" style="width: 100px; height: auto;">\n        `;marker.position=place.location;marker.content=content;infowindow.setContent(content);infowindow.open(map.innerMap,marker)}))}document.addEventListener("DOMContentLoaded",init);document.addEventListener("DOMContentLoaded",(()=>{const backToTopButton=document.getElementById("back-to-top");window.addEventListener("scroll",(()=>{if(window.scrollY>300){backToTopButton.classList.add("show")}else{backToTopButton.classList.remove("show")}const navbar=document.querySelector(".navbar");const mainContent=document.querySelector("main");if(navbar){if(window.scrollY>50){navbar.classList.add("scrolled");if(mainContent){mainContent.classList.add("navbar-scrolled")}}else{navbar.classList.remove("scrolled");if(mainContent){mainContent.classList.remove("navbar-scrolled")}}}}));if(backToTopButton){backToTopButton.addEventListener("click",(()=>{window.scrollTo({top:0,behavior:"smooth"})}))}const anchorLinks=document.querySelectorAll('a[href^="#"]');anchorLinks.forEach((link=>{link.addEventListener("click",(function(e){if(this.getAttribute("href")!=="#"&&!this.classList.contains("carousel-control-prev")&&!this.classList.contains("carousel-control-next")){e.preventDefault();const target=document.querySelector(this.getAttribute("href"));if(target){const navbarHeight=document.querySelector(".navbar").offsetHeight;window.scrollTo({top:target.offsetTop-navbarHeight,behavior:"smooth"})}}}))}));const getDirectionsBtn=document.getElementById("get-directions");if(getDirectionsBtn){getDirectionsBtn.addEventListener("click",(e=>{e.preventDefault();const destination=encodeURIComponent("84 Warren Ave, Marlborough, MA 01752");window.open(`https://www.google.com/maps/dir/?api=1&destination=${destination}`,"_blank")}))}if(typeof WOW!=="undefined"){(new WOW).init()}else{console.error("WOW.js is not loaded")}const carouselElement=document.querySelector("#carouselExampleControls");if(window.innerWidth<=768&&carouselElement){const carouselItems=carouselElement.querySelectorAll(".carousel-item");carouselItems.forEach(((item,index)=>{if(index===0){item.classList.add("active")}else{item.classList.remove("active")}}));$("#carouselExampleControls").carousel({interval:2e3})}}));